function findLongestWordLength(str) {

let total = str.split(' ')
let longestword = 0

for( let i = 0; i < total.length ; i++) {
    if(total[i].length > longestword) {
        longestword = total[i].length
    } 
    }
    return longestword;
}

findLongestWordLength("The quick brown fox jumped over the lazy dog");
