function largestOfFour(arr) {
  // Initialize an empty array to hold the largest numbers
  let largestNumbers = [];

  // Loop through each sub-array in the main array
  for (let i = 0; i < arr.length; i++) {
    // Find the largest number in the current sub-array
    let largestNumber = arr[i][0];
    for (let j = 1; j < arr[i].length; j++) {
      if (arr[i][j] > largestNumber) {
        largestNumber = arr[i][j];
      }
    }

    // Push the largest number to the largestNumbers array
    largestNumbers.push(largestNumber);
  }

  // Return the array of largest numbers
  return largestNumbers;
}
