function quickCheck(arr, elem) {
  // Check if the element exists in the array using indexOf
  return arr.indexOf(elem) !== -1;
}

// Test different cases
console.log(quickCheck(['squash', 'onions', 'shallots'], 'mushrooms')); // should return false
console.log(quickCheck(['squash', 'onions', 'shallots'], 'onions')); // should return true
console.log(quickCheck([1, 2, 3, 4, 5], 3)); // should return true
console.log(quickCheck([1, 2, 3, 4, 5], 6)); // should return false
