function steamrollArray(arr) {
  const compacted = []
  for (let i = 0; i < arr.length; i++) {
    if (Array.isArray(arr[i])) {
      compacted.push(...steamrollArray(arr[i]))
    } else {
      compacted.push(arr[i])
    }
  }
  // I'm a steamroller, baby
  return compacted;
}

