function rot13(str) { // LBH QVQ VG!
let result = "";

for (let i = 0; i < str.length; i++) {
  let char = str[i]

  if (char.match(/[a-zA-Z]/)) {
    let charCode = char.charCodeAt(0)

    let newChar = String.fromCharCode (((charCode - 65 +13) % 26) + 65)

    result += newChar
  } else {
    result += char
  }
}
  return result
}  


